import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { API_URL } from '../config';

const ShowGoal = () => {
  const [goalRecords, setGoalRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem('userEmail');
        const responseUser = await axios.get(`${API_URL}/api/user/fetchuser`, {
          params: {
            email: email,
          },
        });
        const userid = responseUser.data._id;

        const responseGoal = await axios.get(`${API_URL}/api/goal/get-goals/${userid}`);
        setGoalRecords(responseGoal.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleDeleteGoal = async (goalId) => {
    try {
      await axios.delete(`${API_URL}/api/goal/delete-goal/${goalId}`);
      setGoalRecords((prevRecords) => prevRecords.filter((record) => record._id !== goalId));
    } catch (error) {
      console.error('Error deleting goal record:', error);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Goal Records</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {goalRecords && goalRecords.length > 0 ? (
          goalRecords.map((record) => (
            <div key={record._id} className="bg-white rounded-md p-4 shadow-md">
              <h3 className="text-lg text-deep-blue font-playfair font-semibold mb-2">{record.title}</h3>
              <p className="text-dark-grey mb-2">{`Amount: ${record.amount}`}</p>
              <p className="text-dark-grey mb-2">{`Target Date: ${new Date(record.targetDate).toLocaleDateString()}`}</p>
              <p className="text-dark-grey">{`Description: ${record.description}`}</p>
              <div className="flex justify-between mt-4">
                <button
                  className="bg-deep-red text-white px-4 py-2 rounded hover:bg-opaque-black"
                  onClick={() => handleDeleteGoal(record._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>No goal records available.</p>
        )}
      </div>
    </div>
  );
};

export default ShowGoal;
