// Import necessary components and libraries
import React, { useState, useEffect } from 'react';
import AddIncomeForm from '../../component/Addincomeform';
import AddExpenseForm from '../../component/Addexpenseform';
import IncomeRecords from '../../component/showincome';
import ExpenseRecords from '../../component/showexpense';
import image from '../../images/imagebguser.avif';
import { getBalance, getSpendingSummary } from '../../api'; // You need to create these API functions

export default function Userpage() {
  const userEmail = localStorage.getItem('userEmail');
  const [showIncomeForm, setShowIncomeForm] = useState(false);
  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [showIncomeRecords, setShowIncomeRecords] = useState(false);
  const [showExpenseRecords, setShowExpenseRecords] = useState(false);
  const [balance, setBalance] = useState(0);
  const [spendingSummary, setSpendingSummary] = useState([]);

  useEffect(() => {
    // Fetch balance and spending summary when the component mounts
    const fetchData = async () => {
      try {
        const balanceData = await getBalance(userEmail);
        const spendingSummaryData = await getSpendingSummary(userEmail);

        setBalance(balanceData.balance);
        setSpendingSummary(spendingSummaryData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [userEmail]);

  const handleShowIncomeForm = () => {
    // Function to handle showing the income form
    setShowIncomeForm(true);
    setShowExpenseForm(false);
    setShowIncomeRecords(false);
    setShowExpenseRecords(false);
  };

  // ... (similar functions for handling other actions)

  return (
    <>
      {userEmail ? (
        <div className="mt-32 flex flex-col items-center justify-center space-y-4 ">
          <div
            className="md:w-3/5 m-4 md:h-full mb-4"
            style={{ backgroundImage: `url(${image})`, backgroundSize: 'cover', backgroundposition: 'center' }}
          >
            <div className="m-4">
              {/* ... (existing code) */}
              <h2 className="text-2xl font-bold mb-4 text-deep-blue">Financial Overview</h2>
              <p className="text-lg mb-4 text-deep-blue">Balance: ${balance}</p>
              {/* You can customize the format of the spending summary */}
              <div className="text-deep-blue">
                <h3 className="text-lg font-bold mb-2">Spending Summary</h3>
                <ul>
                  {spendingSummary.map((item) => (
                    <li key={item.category}>
                      {item.category}: ${item.amount}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>

          {/* ... (existing code) */}
        </div>
      ) : (
        <></>
      )}
    </>
  );
}
