import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_URL } from '../config';

const AddGoalForm = ({ onClose }) => {
  const history = useNavigate();
  const [title, setTitle] = useState('');
  const [targetAmount, setTargetAmount] = useState('');
  const [deadline, setDeadline] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const email = localStorage.getItem('userEmail');
    let userId = "";

    try {
      const res = await axios.get(`${API_URL}/api/user/fetchuser`, {
        params: {
          email: email,
        },
      });
      if (res.data && res.data._id) {
        userId = res.data._id;
      }
    } catch (e) {
      console.log(e);
    }

    try {
      const response = await axios.post(`${API_URL}/api/goals/add-goal`, {
        title,
        targetAmount,
        deadline,
        category,
        description,
        userId,
      });

      if (response.data === "exist") {
        alert("Goal already added");
      } else if (response.data === "notexist") {
        // success
        alert("Goal successfully added");
        history("/userpage");
      }
    } catch (e) {
      console.log(e);
      alert("Invalid Information");
    }

    onClose();
  };

  return (
    <div className="bg-white p-4 shadow-md rounded-md mt-4 md:w-3/4">
      <h2 className="text-xl font-semibold mb-4 text-black">Add Goal</h2>
      <form onSubmit={handleSubmit}>
        {/* Similar structure for input fields as in AddIncomeForm */}
        <label>Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border border-gray-300 text-black rounded-md p-2 mb-4 w-full"
          placeholder="Enter goal title..."
          required
        />

        {/* Add other input fields for target amount, deadline, category, and description */}

        <button
          type="submit"
          className="bg-blue-500 text-white hover:bg-dark-grey py-2 rounded-md w-full"
        >
          Add Goal
        </button>
      </form>
    </div>
  );
};

export default AddGoalForm;
